```

```typescript
// app.component.ts
import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { JwtHelperService } from '@auth0/angular-jwt';

interface User {
  name: string;
  password: string;
}

interface Vehicle {
  make: string;
  model: string;
  year: number;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  private baseUrl = 'http://localhost:8080';
  private httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
    })
  };
  private jwtHelper = new JwtHelperService();
  private token: string;

  public user: User = {
    name: '',
    password: '',
  };
  public vehicle: Vehicle = {
    make: '',
    model: '',
    year: null,
  };
  public vehicles: Vehicle[] = [];
  public isAdmin: boolean;
  public loggedIn = false;

  constructor(private http: HttpClient) {}

  public login() {
    this.http.post(`${this.baseUrl}/login`, this.user, this.httpOptions)
      .subscribe((response: { token: string }) => {
        this.token = response.token;
        localStorage.setItem('token', this.token);
        this.loggedIn = true;

        const decodedToken = this.jwtHelper.decodeToken(this.token);
        this.isAdmin = decodedToken.role === 'admin';

        this.getVehicles();
      });
  }

  public createVehicle() {
    this.http.post(`${this.baseUrl}/vehicles`, this.vehicle, {
      headers: new HttpHeaders({
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${this.token}`,
      })
    }).subscribe(() => {
      this.vehicle = {
        make: '',
        model: '',
        year: null,
      };
      this.getVehicles();
    });
  }

  private getVehicles() {
    this.http.get(`${this.baseUrl}/vehicles`, {
      headers: new HttpHeaders({
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${this.token}`,
      })
    }).subscribe((response: { vehicles: Vehicle[] }) => {
      this.vehicles = response.vehicles;
    });
  }

  ngOnInit() {
    const token = localStorage.getItem('token');
    if (token && !this.jwtHelper.isTokenExpired(token)) {
      this.token = token;
      this.loggedIn = true;

      const decodedToken = this.jwtHelper.decodeToken(this.token);
      this.isAdmin = decodedToken.role === 'admin';

      this.getVehicles();
    }
  }
}
```

这个应用程序将允许用户根据他们的角色注册并保存车辆信息。它将支持管理员，用户和公司角色，并使用 JWT 来确保安全。

